import React, { Component } from 'react'
import ActionButton from "./components/ActionButton"
import './App.scss';


class App extends Component {
  constructor() {
    super()
    this.state = {
      publicScreen: true,
    }
    this.handleScreenChange = this.handleScreenChange.bind(this)
  }

  componentDidMount() {}

  handleScreenChange(screen){
    if (this.state.publicScreen == screen){return}
    var newScreen = 0
    if (this.state.publicScreen){newScreen = false}
    else {newScreen = true}
    this.setState({publicScreen: newScreen})
  }


  render() {
    return (
      <div className="app h-100">
        <div className="sliding-header d-flex">
          <div className={"tab-slider col-6 text-center bold " + (this.state.publicScreen ? 'blue' : null)} onClick={() => this.handleScreenChange(true)}><p>Public</p></div>
          <div className={"tab-slider col-6 text-center bold " + (!this.state.publicScreen ? 'blue' : null)} onClick={() => this.handleScreenChange(false)}><p>Private</p></div>
        </div>
        <div className="row-holder d-flex">
          <div className="row-item d-flex">
            <div className="image-left col-3">
              i am an image
            </div>
            <div className="content-right col-9">
              <h4 className="bold">Title</h4>
              <p>Find other learners looking to rapidly improve their language skills. </p>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default App
