import React, { Component } from 'react'
import ActionButton from "./components/ActionButton"
import './App.scss';


class App extends Component {
  constructor() {
    super()
    this.state = {
      publicScreen: true,
    }
    this.handleScreenChange = this.handleScreenChange.bind(this)
  }

  componentDidMount() {}

  handleScreenChange(screen){
    var newScreen = 0
    if (this.state.publicScreen){newScreen = false}
    else {newScreen = true}
    this.setState({publicScreen: newScreen})
  }


  render() {
    return (
      <div className="app h-100">
        <div className="sliding-header d-flex">
          <div className={"tab-slider col-6 text-center bold " + (this.state.publicScreen ? 'blue' : null)} onClick={this.handleScreenChange}><p>Public</p></div>
          <div className={"tab-slider col-6 text-center bold " + (!this.state.publicScreen ? 'blue' : null)} onClick={this.handleScreenChange}><p>Private</p></div>
        </div>
      </div>
    )
  }
}

export default App
