import React from 'react';

function Category({data, onClick}) {
  return (
    <div className="row-item d-flex" onClick={onClick}>
      <div className="image-left d-flex">
        {data.image ? <img src={data.image} className="image m-auto"/> : null}
      </div>
      <div className="content-right">
        {data.title ? <p className="bold item-content-title">{data.title}</p>: <p className="bold item-content-title">{data}</p>}
        {data.description ? <p>{data.description}</p> : null}
      </div>
    </div>
  )
}

export default Category
