import React, { Component } from 'react'
import ActionButton from "./components/ActionButton"
import Category from "./components/Category"
import './App.scss';

const categoryData = {
  seriousLearning: {
    title: "Serious learning",
    description: "Find other learners looking to rapidly improve their language skills",
    image: "/alex.png"
  },
  relaxedLearning: {
    title: "Relaxed learning",
    description: "Meet language learners interested in chatting while improving their language skills.",
    image: "/alex.png" 
  },
  justChatting: {
    title: "Just chatting",
    description: "Meet and chat with Tandem members from all over the world.",
    image: "/alex.png" 
  },
  hobbiesAndInterests: {
    title: "Hobbies and interests",
    description: "Find out what people do for fun around the globe.",
    image: "/alex.png" 
  },
  tandemPair: {
    title: "Tandem Pair",
    description: "Join a group with another English speaker and two Spanish speakers.",
    image: "/alex.png" 
  }
}

class App extends Component {
  constructor() {
    super()
    this.state = {
      publicScreen: true,
    }
    this.handleScreenChange = this.handleScreenChange.bind(this)
  }

  componentDidMount() {}

  handleScreenChange(screen){
    if (this.state.publicScreen == screen){return}
    var newScreen = 0
    if (this.state.publicScreen){newScreen = false}
    else {newScreen = true}
    this.setState({publicScreen: newScreen})
  }


  render() {
    return (
      <div className="app h-100">
        <div className="sliding-header d-flex">
          <div className={"tab-slider col-6 text-center bold " + (this.state.publicScreen ? 'blue' : null)} onClick={() => this.handleScreenChange(true)}><p>Public</p></div>
          <div className={"tab-slider col-6 text-center bold " + (!this.state.publicScreen ? 'blue' : null)} onClick={() => this.handleScreenChange(false)}><p>Private</p></div>
        </div>
        {this.state.publicScreen 
        ? 
          <div className="row-holder d-flex">
            {Object.values(categoryData).forEach(category => {
              return <p>{category}</p>
              //<Category data={category}/>
            })
            }
          </div>
        : 
          <div>
            <p>Looks like you don't have any group chats yet...</p>
            <ActionButton text="Start a private group chat"/>
          </div>
        }
      </div>
    )
  }
}

export default App
